import React, {useEffect, useState} from 'react'
import { useLocation, useNavigate } from 'react-router-dom';
import getScrapedData from '../../adapters/getScrapedData'
import SearchCards from './searchcards'
import SearchBar from '../searchbar/searchbar'

export interface Article{
    description: string
    image: string
    pubDate: string
    sentiment: number
    site_name: string
    title: string
    url: string
}

export interface ArticleList{
    data: Article[]
}

const SearchPage = () =>{
    const location = useLocation()
    let navigate = useNavigate()
    const [search, setSearch] = useState('')
    const [data, setData] = useState({} as ArticleList)

    //get search state from current url
    useEffect(()=>{
        const query = location.search.split('=')[1]
        if(!query)
            navigate("../", { replace: true });
        setSearch(query)
    },[location.search])

    //once url is set, call api
    useEffect(()=>{
        setData({} as ArticleList)
        if(!search) return
        getScrapedData(search).then(resp=>setData(resp))
    },[search])
    return(
        <div className="p-3">
            <SearchBar intial={search}/>
            Showing results for {search}
            {data.data?
                <SearchCards {...data}/>
                :"Loading..."
            }
        </div>
    )
}
export default SearchPage
