import React, {useState, useEffect} from 'react'
import minus from '../../images/minus.png'
import down from '../../images/down.png'
import up from '../../images/up.png'
import {SentimentData} from './searchcards'

const SentimentCard = ({sentiment,logo_url, price, prediction}:SentimentData) =>{
    const [diff, setDiff] = useState(0)
    let icon = <img src={minus} alt="minus" className="h-8"/>
    if(sentiment < 0)
        icon = <img src={down} alt="down" className="h-8"/>
    if(sentiment > 0)
        icon = <img src={up} alt="up" className="h-8"/>
    useEffect(()=>{
        setDiff(Number(((prediction||0)-(price||0)).toFixed(2)))
        console.log(prediction, price)
    },[price, prediction])
    return(
        <div className="border border-gray-200 flex items-center my-4 rounded-xl p-2 shadow-xl h-32 justify-center">
            {logo_url?<img src={logo_url} alt="" className="w-12 h-12 mr-3"/>:null}
            <div className="flex flex-col">
                <div className="flex items-center">
                    <h1 className="text-xl mr-2">Overall Sentiment:</h1>
                    {icon}
                </div>
                <div>
                     <h1>Our Prediction: {Number((prediction).toFixed(2))} ({diff})</h1>
                </div>
            </div>

        </div>
    )
}
export default SentimentCard
